import{_ as e,o as a,c as t,b as i}from"./app.d2d47940.js";const u=JSON.parse('{"title":"Vite的特性","description":"","frontmatter":{},"headers":[],"relativePath":"knowledges/frontend/build-tool/vite/研究Vite.md","lastUpdated":1678620975000}'),n={name:"knowledges/frontend/build-tool/vite/研究Vite.md"},o=i('<h1 id="vite的特性" tabindex="-1">Vite的特性 <a class="header-anchor" href="#vite的特性" aria-hidden="true">#</a></h1><p><img src="https://cdn.nlark.com/yuque/0/2023/png/743297/1675066455491-3e0d41e2-7629-46b0-b5f2-64944ebfd435.png#averageHue=%23f8f7f7&amp;clientId=u7be2d503-ab5e-4&amp;from=paste&amp;height=500&amp;id=u86154b8d&amp;name=image.png&amp;originHeight=450&amp;originWidth=1195&amp;originalType=binary&amp;ratio=1&amp;rotation=0&amp;showTitle=false&amp;size=48362&amp;status=done&amp;style=none&amp;taskId=u6abbe95c-6058-4256-8367-94ee51cd664&amp;title=&amp;width=1327.7778129518776" alt="image.png"></p><ol><li><strong>极速的服务启动</strong>：使用原生 ESM 文件，无需打包!</li><li><strong>轻量快速的热重载</strong>：无论应用程序大小如何，都始终极快的模块热替换（HMR）</li><li><strong>丰富的功能</strong>：对 TypeScript、JSX、CSS 等支持开箱即用。</li><li><strong>优化的构建</strong>：可选 “多页应用” 或 “库” 模式的预配置 Rollup 构建</li><li><strong>通用的插件</strong>：在开发和构建之间共享 Rollup-superset 插件接口。</li><li><strong>完全类型化的API</strong>：灵活的 API 和完整的 TypeScript 类型。</li></ol><h1 id="vite解决的问题" tabindex="-1">Vite解决的问题 <a class="header-anchor" href="#vite解决的问题" aria-hidden="true">#</a></h1><ol><li>大型应用模块多时，基于 JavaScript 开发的工具有性能瓶颈，启动开发服务器慢；HMR慢，且重新加载页面会消除应用的当前状态</li><li>其他打包工具开发效率不够快</li><li>冷启动开发服务器时，须先抓取并构建整个应用的资源，才能提供服务</li></ol><h1 id="vite的工作方式" tabindex="-1">Vite的工作方式 <a class="header-anchor" href="#vite的工作方式" aria-hidden="true">#</a></h1><p>Vite 通过在一开始将应用中的模块区分为 <strong>依赖</strong> 和 <strong>源码</strong> 两类，改进了开发服务器启动时间。 <strong>依赖处理</strong>：Vite 使用 esbuild 预构建依赖。esbuild 使用 Go 编写，并且比以 JavaScript 编写的打包器预构建依赖快 10-100 倍。 <strong>源码处理</strong>：Vite 以 原生 ESM 方式提供源码。Vite 只需要在浏览器请求源码时进行转换并按需提供源码。根据情景动态导入代码，即只在当前屏幕上实际使用时才会被处理。 <strong>HTTP请求</strong>：Vite 同时利用 HTTP 头来加速整个页面的重新加载（再次让浏览器为我们做更多事情）：源码模块的请求会根据 304 Not Modified 进行协商缓存，而依赖模块请求则会通过 Cache-Control: max-age=31536000,immutable 进行强缓存</p><p><img src="https://cdn.nlark.com/yuque/0/2023/png/743297/1675068524643-c16e7107-c5a6-4dfe-9b0e-e289ad35e99b.png#averageHue=%23efedeb&amp;clientId=u7be2d503-ab5e-4&amp;from=paste&amp;height=459&amp;id=u5e22f30e&amp;name=image.png&amp;originHeight=413&amp;originWidth=701&amp;originalType=binary&amp;ratio=1&amp;rotation=0&amp;showTitle=false&amp;size=30213&amp;status=done&amp;style=none&amp;taskId=u20f828ad-51bd-4c61-a3fd-547188a8ee8&amp;title=&amp;width=778.8889095223985" alt="image.png"><img src="https://cdn.nlark.com/yuque/0/2023/png/743297/1675068552911-138467c3-3fd8-4702-8fb8-4a9b994d9b39.png#averageHue=%23e4e3e2&amp;clientId=u7be2d503-ab5e-4&amp;from=paste&amp;height=453&amp;id=u1d7a1414&amp;name=image.png&amp;originHeight=408&amp;originWidth=697&amp;originalType=binary&amp;ratio=1&amp;rotation=0&amp;showTitle=false&amp;size=34350&amp;status=done&amp;style=none&amp;taskId=u695a8662-fdc2-4eb8-a036-fe62f671d1c&amp;title=&amp;width=774.4444649602166" alt="image.png"></p>',8),p=[o];function r(s,m,d,l,g,c){return a(),t("div",null,p)}const f=e(n,[["render",r]]);export{u as __pageData,f as default};
