import{_ as e,o as a,c as s,b as n}from"./app.d2d47940.js";const u=JSON.parse('{"title":"VMware下安装CentOS7","description":"","frontmatter":{},"headers":[{"level":2,"title":"配置网络","slug":"配置网络","link":"#配置网络","children":[]}],"relativePath":"knowledges/tech/server/linux-centos/Linux00 - VMware下安装CentOS7.md","lastUpdated":1678618356000}'),t={name:"knowledges/tech/server/linux-centos/Linux00 - VMware下安装CentOS7.md"},l=n(`<h1 id="vmware下安装centos7" tabindex="-1">VMware下安装CentOS7 <a class="header-anchor" href="#vmware下安装centos7" aria-hidden="true">#</a></h1><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404074217-775986.png#alt=image-20200404074217273" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404074252-283236.png#alt=image-20200404074252455" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404074332-468638.png#alt=image-20200404074332049" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404075937-468793.png#alt=image-20200404075936874" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404080028-328414.png#alt=image-20200404080028145" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404081456-875860.png#alt=image-20200404081454575" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404081633-580551.png#alt=image-20200404081632823" alt=""></p><p>开启虚拟机</p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404081903-763402.png#alt=image-20200404081903293" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404082133-563310.png#alt=image-20200404082133533" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404082253-871800.png#alt=image-20200404082253703" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404082347-391156.png#alt=image-20200404082347159" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404082520-923770.png#alt=image-20200404082520059" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404082547-791975.png#alt=image-20200404082547181" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404082601-10308.png#alt=image-20200404082601437" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404082704-390824.png#alt=image-20200404082704443" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404082749-871072.png#alt=image-20200404082749718" alt=""></p><p>等待安装完成</p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404084327-377459.png#alt=image-20200404084326576" alt=""></p><p>登录完成</p><h2 id="配置网络" tabindex="-1">配置网络 <a class="header-anchor" href="#配置网络" aria-hidden="true">#</a></h2><p>配置虚拟机</p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404084729-841068.png#alt=image-20200404084729244" alt=""></p><p>进入目录</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/sysconfig/network-scripts</span></span>
<span class="line"></span></code></pre></div><p>查看网卡</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ls</span></span>
<span class="line"></span></code></pre></div><p>我这里的是ifcfg-ens33</p><p>编辑这个文件</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">vi</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ifcfg-ens33</span></span>
<span class="line"></span></code></pre></div><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404085550-73601.png#alt=image-20200404085549253" alt=""></p><p>修改成如下配置</p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404090154-572857.png#alt=image-20200404090148572" alt=""></p><p>保存配置退出</p><p>按esc键 输入<code>:wq</code>，按回车即可</p><blockquote><p>给IPADDR指定ip地址，前面的192.168.1这三位数一定要与本机的ip一样</p></blockquote><blockquote><p>打开cmd，输入<code>ipconfig</code>查看本机的ip</p></blockquote><blockquote><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404090441-977429.png#alt=image-20200404090439788" alt=""></p></blockquote><blockquote><p>我本机的ip是192.168.1开头的，所以我给虚拟机分配的ip是192.168.1.6。</p></blockquote><p>重启网络</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">service</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">network</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">restart</span></span>
<span class="line"></span></code></pre></div><p>配置VMware的虚拟网络</p><p>点击VM的“编辑” &gt; “虚拟网络编辑器”</p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404084916-506090.png#alt=image-20200404084915876" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404091044-677193.png#alt=image-20200404091044168" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404091321-728122.png#alt=image-20200404091318978" alt=""></p><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200404091435-698769.png#alt=image-20200404091435468" alt=""></p>`,48),p=[l];function i(c,o,g,m,h,r){return a(),s("div",null,p)}const _=e(t,[["render",i]]);export{u as __pageData,_ as default};
