import{_ as e,o as i,c as d,b as l}from"./app.d2d47940.js";const x=JSON.parse('{"title":"Linux 编辑器","description":"","frontmatter":{"date":"2020-01-24T00:00:00.000Z","categories":["服务器"],"tags":["linux"]},"headers":[{"level":2,"title":"vim","slug":"vim","link":"#vim","children":[{"level":3,"title":"运行模式","slug":"运行模式","link":"#运行模式","children":[]},{"level":3,"title":"命令","slug":"命令","link":"#命令","children":[]}]},{"level":2,"title":"nano","slug":"nano","link":"#nano","children":[{"level":3,"title":"命令","slug":"命令-1","link":"#命令-1","children":[]}]},{"level":2,"title":"快捷键","slug":"快捷键","link":"#快捷键","children":[]}],"relativePath":"knowledges/tech/server/linux-centos/Linux07 - Linux 编辑器.md","lastUpdated":1678618356000}'),n={name:"knowledges/tech/server/linux-centos/Linux07 - Linux 编辑器.md"},a=l('<h1 id="linux-编辑器" tabindex="-1">Linux 编辑器 <a class="header-anchor" href="#linux-编辑器" aria-hidden="true">#</a></h1><h2 id="vim" tabindex="-1">vim <a class="header-anchor" href="#vim" aria-hidden="true">#</a></h2><h3 id="运行模式" tabindex="-1">运行模式 <a class="header-anchor" href="#运行模式" aria-hidden="true">#</a></h3><p>编辑模式：等待编辑命令输入</p><p>插入模式：编辑模式下，输入 <code>i</code> 进入插入模式，插入文本信息</p><p>命令模式：在编辑模式下，输入 <code>:</code> 进行命令模式</p><h3 id="命令" tabindex="-1">命令 <a class="header-anchor" href="#命令" aria-hidden="true">#</a></h3><p><code>:q</code> 直接退出vi</p><p><code>:wq</code> 保存后退出vi ，并可以新建文件</p><p><code>:q!</code> 强制退出</p><p><code>:w file</code> 将当前内容保存成某个文件</p><p><code>:set number</code> 在编辑文件显示行号</p><p><code>:set nonumber</code> 在编辑文件不显示行号</p><h2 id="nano" tabindex="-1">nano <a class="header-anchor" href="#nano" aria-hidden="true">#</a></h2><p>nano 是一个字符终端的文本编辑器，有点像 DOS 下的 editor 程序。它比 vi/vim 要简单得多，比较适合 Linux 初学者使用。某些 Linux 发行版的默认编辑器就是 nano。</p><h3 id="命令-1" tabindex="-1">命令 <a class="header-anchor" href="#命令-1" aria-hidden="true">#</a></h3><ul><li>保存：ctrl + o</li><li>搜索：ctrl + w</li><li>上一页：ctrl + y</li><li>下一页：ctrl + v</li><li>退出：ctrl + x</li></ul><h2 id="快捷键" tabindex="-1">快捷键 <a class="header-anchor" href="#快捷键" aria-hidden="true">#</a></h2><ol><li>拷贝当前行：（正常模式下） <code>yy</code>，拷贝当前向下5行：<code>5yy</code></li><li>删除当前行： （正常模式下）<code>dd</code>，删除当前向下5行：<code>5dd</code></li><li>搜索单词：（正常模式下）输入<code>/关键字</code>，如<code>/hello</code>。按下回车查找，按n下一个</li><li>显示行号：（命令模式下）<code>:set nu</code></li><li>取消显示行号：（命令模式下）<code>:set nonu</code></li><li>转到文档顶部：（正常模式下）<code>G</code></li><li>转到文档底部：（正常模式下）<code>gg</code></li></ol><p><img src="https://alanlee-image-bed.oss-cn-shenzhen.aliyuncs.com/note_images/20200326085518-483348.jpeg#alt=vi%E5%92%8Cvim%E5%BF%AB%E6%8D%B7%E9%94%AE%E4%B8%80%E8%A7%88%E5%9B%BE" alt=""></p>',20),c=[a];function o(t,r,s,h,u,p){return i(),d("div",null,c)}const v=e(n,[["render",o]]);export{x as __pageData,v as default};
